// v2.3 - Debug and validate frames structure
console.log('[Transform v2.3] Input frames:', frames);
console.log('[Transform v2.3] frames type:', typeof frames, 'isArray:', Array.isArray(frames));
if (frames && frames.length > 0) {
  console.log('[Transform v2.3] frames[0]:', frames[0]);
  console.log('[Transform v2.3] frames[0].fields:', frames[0].fields);
  if (frames[0].fields && frames[0].fields.length > 0) {
    console.log('[Transform v2.3] frames[0].fields[0]:', frames[0].fields[0]);
  }
}

// Safe access with validation
if (!frames || frames.length === 0) {
  console.error('[Transform v2.3] No frames provided!');
  return [];
}

if (!frames[0].fields || frames[0].fields.length === 0) {
  console.error('[Transform v2.3] No fields in frames[0]!');
  return [];
}

const apiData = frames[0].fields[0].values[0];
console.log('[Transform v2.3] Raw API data:', apiData);

const items = apiData?.data?.items || [];
console.log('[Transform v2.3] Items count:', items.length);

// Create one TDataFrame for each item (each becomes a series)
const result = items.map((item, index) => {
  const intervals = item.intervals || [];
  const seriesName = item.p || `Series ${index + 1}`;

  console.log(`[Transform v2.3] Series ${index + 1} (${seriesName}): ${intervals.length} points`);

  return {
    name: seriesName,
    fields: [
      {
        name: 'time',
        type: 'time',
        config: {},
        values: intervals.map(pt => pt.T)
      },
      {
        name: 'value',
        type: 'number',
        config: {},
        values: intervals.map(pt => pt.V)
      }
    ],
    length: intervals.length
  };
});

console.log('[Transform v2.3] Output:', result.length, 'series');
return result;
